public enum Sexo {
    MASCULINO("Masculino",'M'),
    FEMININO("Feminino",'F');
    
    private String tipo;
    private char abrev;
    
    Sexo(String tipo, char abrev){
        this.tipo = tipo;
        this.abrev = abrev;
    }
    
    public char getAbrev(){
        return this.abrev;
    }
    
    public String getTipo(){
        return this.tipo;
    }
}
